changeSet.sql:
CREATE TABLE "pins" ( "id" bigint not null primary key, "pnfsid" varchar(256) not null, "request_id" varchar(256) null, "created_at" timestamp not null, "expires_at" timestamp null, "uid" bigint not null, "gid" bigint not null, "state" varchar(256) not null, "pool" varchar(256) null, "sticky" varchar(256) null );
CREATE UNIQUE INDEX "idx_pnfsid_id" ON "pins" ("pnfsid", "request_id"); CREATE INDEX "idx_state" ON "pins" ("state"); CREATE INDEX "idx_expires_at" ON "pins" ("expires_at");
CREATE SEQUENCE "pin_sequence" START WITH 1 INCREMENT BY 10;
